import React from 'react';
import '../../style/topnav.css'
import { useSelector, useDispatch } from 'react-redux';
import { RootState } from '../../store/store';
import { setSelectedOrganisation } from '../organisationSlice';
import { getResultsFromAPI } from '../../searchfeature/result';

export const TopNav = () => {
    const dispatch = useDispatch();

    const { organisation } = useSelector((state: RootState) => ({
        organisation: state.organisation,
    }));

    const handleOrgChange = (e: React.ChangeEvent<HTMLSelectElement>) => {
        const orgId = e.target.value;
        dispatch(setSelectedOrganisation(orgId));
        dispatch(getResultsFromAPI()); 
    };

    return (
        <div id="topnav" data-testid="top-nav">
            <h2>Patient Management</h2>

            <div id="right-side">
                <label className="light">Your Organisation:</label>
                <select value={organisation.selectedId || ''} onChange={handleOrgChange}>
                    {organisation.list.map(org => (
                        <option key={org.id} value={org.id}>
                            {org.name}
                        </option>
                    ))}
                </select>
            </div>
        </div>
    );
};
