import { createSlice, PayloadAction } from '@reduxjs/toolkit';

export interface Organisation {
    id: string;
    name: string;
}

interface OrganisationState {
    list: Organisation[];
    selectedId: string | null;
}

const initialState: OrganisationState = {
    list: [],
    selectedId: null,
};

export const organisationSlice = createSlice({
    name: 'organisation',
    initialState,
    reducers: {
        setOrganisations: (state, action: PayloadAction<Organisation[]>) => {
            state.list = action.payload;
            if (!state.selectedId && action.payload.length > 0) {
                state.selectedId = action.payload[0].id; // default select first
            }
        },
        setSelectedOrganisation: (state, action: PayloadAction<string>) => {
            state.selectedId = action.payload;
        },
    },
});

export const { setOrganisations, setSelectedOrganisation } = organisationSlice.actions;

export default organisationSlice.reducer;
